import React, { useState } from 'react';
import { addBook } from '../services/bookService';

const AddBook = () => {
  const [book, setBook] = useState({
    name: '',
    author: '',
    genre: '',
    price: '',
    publisher: '',
  });

  const handleChange = (e) => {
    setBook({ ...book, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addBook(book);
    setBook({ name: '', author: '', genre: '', price: '', publisher: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Book</h2>
      <input
        type="text"
        name="name"
        value={book.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="text"
        name="author"
        value={book.author}
        onChange={handleChange}
        placeholder="Author"
        required
      />
      <input
        type="text"
        name="genre"
        value={book.genre}
        onChange={handleChange}
        placeholder="Genre"
        required
      />
      <input
        type="text"
        name="price"
        value={book.price}
        onChange={handleChange}
        placeholder="Price"
        required
      />
      <input
        type="text"
        name="publisher"
        value={book.publisher}
        onChange={handleChange}
        placeholder="Publisher"
        required
      />
      <button type="submit">Add Book</button>
    </form>
  );
};

export default AddBook;
