import React, { useState } from 'react';
import { addUser } from '../services/userService';

const AddUser = () => {
  const [user, setUser] = useState({
    name: '',
    surname: '',
    email: '',
    subscriptionType: '',
    subscriptionDate: '',
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addUser(user);
    setUser({ name: '', surname: '', email: '', subscriptionType: '', subscriptionDate: '' });
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <h2>Add User</h2>
        <input
          type="text"
          name="name"
          value={user.name}
          onChange={handleChange}
          placeholder="Name"
          required
        />
        <input
          type="text"
          name="surname"
          value={user.surname}
          onChange={handleChange}
          placeholder="Surname"
          required
        />
        <input
          type="email"
          name="email"
          value={user.email}
          onChange={handleChange}
          placeholder="Email"
          required
        />
        <input
          type="text"
          name="subscriptionType"
          value={user.subscriptionType}
          onChange={handleChange}
          placeholder="Subscription Type"
          required
        />
        <input
          type="date"
          name="subscriptionDate"
          value={user.subscriptionDate}
          onChange={handleChange}
          placeholder="Subscription Date"
          required
        />
        <button type="submit">Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
